var uiPullrefresh = bui.pullrefresh({
    id: "#uiPullrefresh",
    onRefresh: onRefresh
});

//下拉的时候刷新数据
function onRefresh () {
  // 执行往事件以后需要执行返回函数,第一次执行的时候,还没有返回对象,需要延迟执行
  uiPullrefresh.reverse();
};

// 下拉刷新 js 初始化:

var uiPullRefresh = bui.pullrefresh({
    id: "#uiPullrefresh",
    onRefresh: getData
})

//请求数据 或者其它操作
function getData(){
    bui.ajax({
      url: "",
      data: {}
    }).done(function(res){

      //请求成功以后还原刷新前状态
      uiPullRefresh.reverse();
    })
}

