//刷新数据
function refresh () {
    var page = 1;
    var pagesize = 5;
    getData(page,pagesize,"html");
}

//新增下一页数据
function getData(page,pagesize,command){
  var command = command || "append";

  // 更新分页信息,如果高度不足会自动请求下一页
  uiScroll.updateCache(page,res.data);

  // 刷新的时候返回位置
  uiScroll.reverse();

}

