// 下拉刷新 js 初始化: 

var uiPullRefresh = bui.pullrefresh({
    id: "#uiPullrefresh",
    onRefresh: getData
})

//请求数据 或者其它操作
function getData(){
    bui.ajax({
      url: "",
      data: {}
    }).then(function(res){

      //请求成功以后还原刷新前状态
      uiPullRefresh.reverse();
    },function(res){

      //请求失败以后还原刷新前状态
      uiPullRefresh.reverse();
    })
}

