// 选项卡 js 初始化:
var tab = bui.tab({
    id:"#tabDynamic",
    menu:"#tabDynamicNav",
    // 1: 声明是动态加载的tab
    autoload: true,
})

// 2: 监听跳转的时候触发
tab.on("to",function (index) {
    // 根据索引加载对应的脚本
    switch(index){
        case 0:
        loader.require(["pages/main/home"],function(main){
            console.log("有回调每次都执行")
        })
        break;
        case 1:
        console.log("没回调只加载一次")
        loader.require(["pages/main/news"])
    }
}).to(0);


