    "n":"北京",
    "c":[{"a":["城区","城区以外"],"n":"密云区"}]
    }],
   title: "所在地区",
   trigger: ".selected-val",
   level: 3,
   field:{
       name: "n",
       data: ["c","a"],
   }
})

\$("#chooseCity").on("click",function (e) {
    uiLevelSelect.show();
})

<!-- 级联菜单 html 对应的结构: -->
<ul class="bui-list">
    <li class="bui-btn bui-box clearactive">
        <div class="bui-label">所在地区</div>
        <div class="span1">
            <div id="chooseCity" class="bui-box">
                <div class="selected-val"></div>
                <div class="selected-val"></div>
                <div class="selected-val"></div>
            </div>
        </div>
    </li>
</ul>

